// âœ… frontend/src/components/MovieList.jsx
import React, { useState } from 'react';
import './MovieList.css';
import MovieModal from './MovieModal';

function MovieList({ movies, favorites, toggleFavorite, isLoggedIn, onMovieClick }) {
  const [ratings, setRatings] = useState(() => {
    const stored = localStorage.getItem('ratings');
    return stored ? JSON.parse(stored) : {};
  });
  const [selectedMovie, setSelectedMovie] = useState(null);

  const handleRating = (movieId, value) => {
    const updated = { ...ratings, [movieId]: value };
    setRatings(updated);
    localStorage.setItem('ratings', JSON.stringify(updated));
  };

  if (!movies || movies.length === 0) {
    return <p className="empty-message">ðŸŽ¬ No movies found. Try searching again.</p>;
  }

  return (
    <div className="movie-grid">
      {movies.map((movie) => {
        const isFav = favorites?.[movie.id];
        return (
          <div
            key={movie.id}
            className="movie-card"
            onClick={() => {
              setSelectedMovie(movie);
              onMovieClick && onMovieClick(movie);
            }}
          >
            <img
              className="poster"
              src={`https://image.tmdb.org/t/p/w200${movie.poster_path}`}
              alt={movie.title}
            />
            <div className="movie-info">
              <h3>{movie.title}</h3>
              <div className="rating-stars">
                {[1, 2, 3, 4, 5].map((val) => (
                  <button
                    key={val}
                    className={`star-button ${ratings[movie.id] === val ? 'active' : ''}`}
                    onClick={(e) => {
                      e.stopPropagation();
                      handleRating(movie.id, val);
                    }}
                  >
                    {val}
                  </button>
                ))}
              </div>
              {isLoggedIn && (
                <button
                  className={`favorite-toggle ${isFav ? 'fav' : ''}`}
                  onClick={(e) => {
                    e.stopPropagation();
                    toggleFavorite(movie);
                  }}
                >
                  {isFav ? 'â˜… Unfavorite' : 'â˜† Favorite'}
                </button>
              )}
            </div>
          </div>
        );
      })}
      {selectedMovie && (
        <MovieModal movie={selectedMovie} onClose={() => setSelectedMovie(null)} />
      )}
    </div>
  );
}

export default MovieList;
