// ✅ frontend/src/components/MovieList.jsx
import React from 'react';
import './MovieList.css';

function MovieList({ movies, favorites, toggleFavorite, isLoggedIn, onMovieClick }) {
  if (!movies || movies.length === 0) {
    return <p className="no-movies">No movies found. Try searching or showing trending.</p>;
  }

  return (
    <div className="movie-list">
      {movies.map((movie) => {
        const isFav = favorites?.[movie.id];

        return (
          <div key={movie.id} className="movie-item" onClick={() => onMovieClick(movie)}>
            <img
              src={`https://image.tmdb.org/t/p/w300${movie.poster_path}`}
              alt={movie.title}
              className="movie-poster"
            />
            <h3 className="movie-title">{movie.title}</h3>
            {isLoggedIn && (
              <button
                className={`favorite-btn ${isFav ? 'active' : ''}`}
                onClick={(e) => {
                  e.stopPropagation();
                  toggleFavorite(movie);
                }}
              >
                {isFav ? '★ Unfavorite' : '☆ Favorite'}
              </button>
            )}
          </div>
        );
      })}
    </div>
  );
}

export default MovieList;
