import React, { useState } from 'react';
import './MovieList.css';

function MovieList({ movies, favorites, toggleFavorite, isLoggedIn }) {
  const [ratings, setRatings] = useState({});

  const handleRating = (movieId, value) => {
    setRatings({ ...ratings, [movieId]: value });
  };

  if (!movies || movies.length === 0) {
    return <p>No movies found. Try searching or showing trending.</p>;
  }

  return (
    <div className="movie-list">
      {movies.map((movie) => {
        const isFav = favorites?.[movie.id];

        return (
          <div key={movie.id} className="movie-item">
            <img
              src={`https://image.tmdb.org/t/p/w200${movie.poster_path}`}
              alt={movie.title}
            />
            <h3>{movie.title}</h3>
            <p>Rating: {ratings[movie.id] || 'Not rated'}</p>
            <div className="rating-buttons">
              {[1, 2, 3, 4, 5].map((val) => (
                <button key={val} onClick={() => handleRating(movie.id, val)}>
                  {val}
                </button>
              ))}
            </div>

            {isLoggedIn && (
              <button
                className={`favorite-button ${isFav ? 'active' : ''}`}
                onClick={() => toggleFavorite(movie)}
              >
                {isFav ? '★ Unstar' : '☆ Star'}
              </button>
            )}
          </div>
        );
      })}
    </div>
  );
}

export default MovieList;
