// frontend/src/components/MovieModal.jsx
import React, { useEffect, useState } from 'react';
import './MovieModal.css';

function MovieModal({ movie, onClose, toggleFavorite, favorites, onRate, ratings }) {
  if (!movie) return null;

  const [trailerKey, setTrailerKey] = useState(null);

  useEffect(() => {
    const fetchTrailer = async () => {
      try {
        const response = await fetch(`/api/trailer/${movie.id}`);
        const data = await response.json();
        const trailer = data.results.find(
          (vid) => vid.site === 'YouTube' && vid.type === 'Trailer'
        );
        if (trailer) setTrailerKey(trailer.key);
      } catch (error) {
        console.error('Error fetching trailer:', error);
      }
    };

    fetchTrailer();
  }, [movie]);

  const isFavorite = favorites?.[movie.id];
  const rating = ratings?.[movie.id] || 0;

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        <button className="close-btn" onClick={onClose}>×</button>
        <h2>{movie.title}</h2>
        <p><strong>Release Date:</strong> {movie.release_date}</p>
        <p>{movie.overview}</p>

        {trailerKey && (
          <div className="trailer">
            <iframe
              width="100%"
              height="315"
              src={`https://www.youtube.com/embed/${trailerKey}`}
              title="YouTube trailer"
              frameBorder="0"
              allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
              allowFullScreen
            />
          </div>
        )}

        <div className="rating-section">
          <p><strong>Your Rating:</strong></p>
          {[1, 2, 3, 4, 5].map((val) => (
            <button
              key={val}
              className={val <= rating ? 'star filled' : 'star'}
              onClick={() => onRate(movie.id, val)}
            >
              ★
            </button>
          ))}
        </div>

        <button
          className={`favorite-btn ${isFavorite ? 'active' : ''}`}
          onClick={() => toggleFavorite(movie)}
        >
          {isFavorite ? '★ Unfavorite' : '☆ Favorite'}
        </button>
      </div>
    </div>
  );
}

export default MovieModal;
