import React, { useState } from 'react';
import './MovieList.css';
import MovieModal from './MovieModal';

function MovieList({ movies }) {
  const [ratings, setRatings] = useState({});
  const [favorites, setFavorites] = useState(() => {
    const stored = localStorage.getItem('favorites');
    return stored ? JSON.parse(stored) : [];
  });
  const [showingFavorites, setShowingFavorites] = useState(false);
  const [selectedMovie, setSelectedMovie] = useState(null);

  const handleRating = (movieId, value) => {
    setRatings({ ...ratings, [movieId]: value });
  };

  const toggleFavorite = (movie) => {
    const exists = favorites.find((f) => f.id === movie.id);
    let updated;

    if (exists) {
      updated = favorites.filter((f) => f.id !== movie.id);
    } else {
      updated = [...favorites, movie];
    }

    setFavorites(updated);
    localStorage.setItem('favorites', JSON.stringify(updated));
  };

  const displayedMovies = showingFavorites ? favorites : movies;

  return (
    <div>
      <button onClick={() => setShowingFavorites((prev) => !prev)}>
        {showingFavorites ? 'üîô Back to All Movies' : '‚≠ê View Favorites'}
      </button>

      <div className="movie-list">
        {displayedMovies.map((movie) => {
          const isFavorite = favorites.some((fav) => fav.id === movie.id);

          return (
            <div
              key={movie.id}
              className="movie-item"
              onClick={() => setSelectedMovie(movie)}
            >
              <img
                src={`https://image.tmdb.org/t/p/w200${movie.poster_path}`}
                alt={movie.title}
              />
              <h3>{movie.title}</h3>
              <p>Rating: {ratings[movie.id] || 'Not rated'}</p>
              <div className="rating-buttons">
                {[1, 2, 3, 4, 5].map((val) => (
                  <button
                    key={val}
                    onClick={(e) => {
                      e.stopPropagation();
                      handleRating(movie.id, val);
                    }}
                  >
                    {val}
                  </button>
                ))}
              </div>
              <button
                onClick={(e) => {
                  e.stopPropagation();
                  toggleFavorite(movie);
                }}
              >
                {isFavorite ? '‚òÖ Unfavorite' : '‚òÜ Favorite'}
              </button>
            </div>
          );
        })}
      </div>

      {selectedMovie && (
        <MovieModal movie={selectedMovie} onClose={() => setSelectedMovie(null)} />
      )}
    </div>
  );
}

export default MovieList;
